import React,{useState}from 'react';
import './App.css'
import CourseInput from './Components/CourseInput/CourseInput';

function App() {
  // Goal Handler begin array
  const [courseGoals, setCourseGoals] = useState([
    { text: 'Do all exercises!', id: 'g1' },
    { text: 'Finish the course!', id: 'g2' }
  ]);

const addGoalHandler = (enteredText) => {
  setCourseGoals(prevGaols => {
    const courseGoals = [{enteredText,id : Math.random().toString()},...prevGaols];
    console.log(courseGoals)
  })
};

  return (
    <div>
      <section id="goalForm">
      <CourseInput onAddGoal={addGoalHandler} items={courseGoals} />
    </section>
    <section></section>
    </div>


  );
}

export default App;
